exports.construct = function(config) {
	config.register_path_get('/', exports.init);	
};

exports.init = function(req, res){
	exports.res = res;
	exports.req = req;

	var entrada = {v1 : exports.req.query.v1, v2 : exports.req.query.v2};	
	exports.somaValores(entrada);	
};

exports.somaValores = function(data) {	
	require('somaValores/index.js').init(data, function(args) {
		exports.convertMoney(args);	
	});
};

exports.convertMoney = function(data) {
	require('convertMoney/index.js').init(data, function(args) {		
		exports.boxView(args);
	});
};

exports.boxView = function(data) {
	require('boxView/index.js').init(data, function(args) {		
		exports.res.render('boxView/index', args);
	});
};