var index = require('./index.js');

exports.test1 = function(callback) {
	index.init({total : 100}, function(args) {
		if(args.dolar == 144){
			callback({msg : "testInit OK", type : "info"});
		}else{
			callback({msg : "testInit Failed ... "+JSON.stringify(args), type : "ERRO"});
		}
	});
};

